import UIKit

protocol ContentListRoutingLogic {
    func navigate(from source: BaseViewController, to destination: BaseViewController)
    func present(from source: BaseViewController, to destination: BaseViewController)
}

//protocol ContentListDataPassing {
//    var dataStore: ContentListDataStore? { get }
//}

class ContentListRouter: NSObject {
    var viewController: ContentListViewController?
//    var dataStore: ContentListDataStore?
}

extension ContentListRouter: ContentListRoutingLogic {
    func navigate(from source: BaseViewController, to destination: BaseViewController) {
        source.navigationController?.pushViewController(destination, animated: true)
    }
    
    func present(from source: BaseViewController, to destination: BaseViewController) {
        source.navigationController?.present(destination, animated: true, completion: nil)
    }
}

